require 'rails_helper'

RSpec.describe 'Post show', type: :feature do
  before(:each) do
    @first_user = User.create(name: 'kidde',
                              photo: 'data:image/jpeg;base64,',
                              bio: 'web developer.')
    @second_user = User.create(name: 'william',
                               photo: 'data:image/jpeg;base64,',
                               bio: 'React developer')

    @first_post = Post.create(author: @first_user, title: 'Post 1',
                              text: 'Lorem Ipsum is not simply random text.')
    @second_post = Post.create(author: @second_user, title:'Post 2',
                               text: 'Lorem Ipsum is not simply random text.')

    Comment.create(post: @first_post, author: @second_user, text: 'Hello')
    Comment.create(post: @second_post, author: @first_user, text: 'I like this article.')
    Comment.create(post: @first_post, author: @first_user, text: 'Fantastic, unbelievable')

    Like.create(post: @first_post, author: @second_user)
    Like.create(post: @first_post, author: @first_user)
  end

  it 'should display post title' do
    visit user_post_path(@first_post, @first_user)
    expect(page).to have_content @first_post.title
  end

  it 'should display post body' do
    visit user_post_path(@second_post, @second_user)
    expect(page).to have_content @second_post.text
  end

  it 'should display post author' do
    visit user_post_path(@first_post, @first_user)
    expect(page).to have_content @first_post.author.name
  end

  it 'should display comments count' do
    visit user_post_path(@first_post, @first_user)
    expect(page).to have_content("Comments: #{@first_post.comments_counter}")
  end

  it 'should display likes count' do
    visit user_post_path(@second_post, @second_user)
    expect(page).to have_content("Likes: #{@second_post.likes_counter}")
  end

  it 'should display commenters name' do
    visit user_post_path(@second_post, @second_user)
    @second_post.comments.each do |comment|
      expect(page).to have_content comment.author.name
    end
  end

  it 'should display comment' do
    visit user_post_path(@second_post, @second_user)
    @second_post.comments.each do |comment|
      expect(page).to have_content comment.text
    end
  end
end
